weatherApp.controller("homeController",["$scope","$location","addressService","weatherService",function(a,d,b,c){a.address=b.address;a.$watch("address",function(){b.address=a.address});a.submit=function(){a.submitted=false;setTimeout(function(){a.submitted=true;b.getLatLong(a.address).$promise.then(function(e){a.formattedAddress=function(){return(e.results["0"].formatted_address)};a.lat=e.results[0].geometry.location.lat;a.lon=e.results[0].geometry.location.lng;a.weatherResult=c.getWeather(a.lat,a.lon)})},300)}}]);weatherApp.controller("forecastController",["$scope",function(a){a.convertToCelsius=function(b){if(b){return Math.round((b-32)/1.8)}else{return null}};a.convertToDate=function(b){if(b){return new Date(b*1000)}else{return null}};a.convertToPercentage=function(b){if(b){return Math.round(b*100)}else{return null}}}]);